#!/bin/bash

set -euo pipefail

for dir in $(go list -f '{{.Dir}}' -find $1); do
    if [ "$dir" != "." ]; then
        pkg=$(basename "$dir")
        file="$dir/${pkg}_main_test.go"
        if [ ! -f "$file" ]; then
            cat > "$file" <<EOF
// Code generated by $0 generator. DO NOT EDIT.
package $pkg

import (
	"testing"

	"github.com/stackrox/rox/pkg/testutils/goleak"
)

func TestMain(m *testing.M) {
	goleak.VerifyTestMain(m)
}
EOF
            echo "Created $file"
        fi
    fi
done
