package rhel

import "strings"

const (
	suffix = "uncertified"
)

// GetUncertifiedLayerName converts the given layer name to
// be identified as uncertified.
func GetUncertifiedLayerName(layerName string) string {
	return layerName + suffix
}

// GetOriginalLayerName takes an uncertified name
// (usually a name generated by GetUncertifiedLayerName)
// and converts it back to its original name.
func GetOriginalLayerName(layerName string) string {
	return strings.TrimSuffix(layerName, suffix)
}
